import { Shop } from './Shop.entity'
import { User } from './User.entity'
import { Item } from './Item.entity'
import { Available } from './Available.entity'

@Entity()
export class Product {

    @PrimaryGeneratedColumn()
    id:number;
    @CreateDateColumn()
    createdAt: Date
    @UpdateDateColumn()
    updatedAt: Date

    @Column("character varying",{
        nullable:false,
        length:255,
        })
    name:string;

    @Column("real")
    price:string;

    @Column("text",{
        nullable:true,
        })
    description:string;

    @Column("character varying",{
        nullable:true,
        unique: false,
        length:255,
        })
    @IsUrl()
    image:string;

    // Relations

    @ManyToOne(type=>Shop, shop=>shop.products, {
      cascadeInsert: true,
      cascadeUpdate: true,
      cascadeRemove: true,
    })
    shop:Shop;

    @ManyToMany(type=>User, user=>user.products)
    users: User[]

    @OneToMany(type=>Item, items=>items.product)
    items: Item[]

    @OneToMany(type=>Available, avaliables=>avaliables.product)
    avaliables: Available[]

}

